import React from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import axios from 'axios'

export default class CreateVehicle extends React.Component {

    state = {
        make: '',
        model: '',
        year: '',
        numberPlate: '',
        staff: '',
        ownedOrLeased: 'Owned',
        cost: '',
        leaseExp: new Date(),
        regoDone: new Date(),
        warrantDone: new Date(),
        odometer: '',
        odometerDate: new Date() 
    }

    // DO I NEED componentDidMount method here? 

    onChangeMake = (e) => {
        this.setState({
            make: e.target.value
        })
    }

    onChangeModel = (e) => {
        this.setState({
            model: e.target.value
        })
    }

    onChangeYear = (e) => {
        this.setState({
            year: e.target.value
        })
    }

    onChangeNumberPlate = (e) => {
        this.setState({
            numberPlate: e.target.value
        })
    }

    onChangeStaff = (e) => {
        this.setState({
            staff: e.target.value
        })
    }

    onChangeOwnedOrLeased = (e) => {
        this.setState({
            ownedOrLeased: e.target.value
        })
    }

    onChangeCost = (e) => {
        this.setState({
            cost: e.target.value
        })
    }

    onChangeLeaseExp = (date) => {
        this.setState({
            leaseExp: date
        })
    }

    onChangeRegoDone = (date) => {
        this.setState({
            regoDone: date
        })
    }

    onChangeWarrantDone = (date) => {
        this.setState({
            warrantDone: date
        })
    }

    onChangeOdometer = (e) => {
        this.setState({
            odometer: e.target.value
        })
    }

    onChangeOdometerDate = (date) => {
        this.setState({
            odometerDate: date
        })
    }
    // NEED TO ADD METHODS FOR ALL FIELDS

    onSubmit = (e) => {
        e.preventDefault()
        const vehicle = {
            make: this.state.make,
            model: this.state.model,
            year: this.state.year,
            numberPlate: this.state.numberPlate,
            staff: this.state.staff,
            ownedOrLeased: this.state.ownedOrLeased,
            cost: this.state.cost,
            leaseExp: this.state.leaseExp,
            regoDone: this.state.regoDone,
            warrantDone: this.state.warrantDone,
            odometer: this.state.odometer,
            odometerDate: this.state.odometerDate
        }

        axios.post('http://localhost:5000/vehicles/add', vehicle)
        .then(res => console.log(res.data))

        window.location = '/'
    }
    // INSTALL DATEPICKER FOR ALL DATE ENTRIES
    render() {
        return(
            <div style={{width: '50%', margin: '0 auto 0 auto'}}>
                <h3>Add Vehicle Entry</h3>
                <form onSubmit={this.onSubmit}>
                    <div className='form-group'>
                        <label>Make: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.make} 
                        onChange={this.onChangeMake}/>
                    </div>
                    <div className='form-group'>
                        <label>Model: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.model} 
                        onChange={this.onChangeModel}/>
                    </div>
                    <div className='form-group'>
                        <label>Year: </label>
                        <input type='number' 
                        required className='form-control' 
                        value={this.state.year} 
                        onChange={this.onChangeYear}/>
                    </div>
                    <div className='form-group'>
                        <label>Plate: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.numberPlate} 
                        onChange={this.onChangeNumberPlate}/>
                    </div>
                    <div className='form-group'>
                        <label>Staff: </label>
                        <input type='text' 
                        className='form-control' 
                        value={this.state.staff} 
                        onChange={this.onChangeStaff}/>
                    </div>
                    <div className='form-group'>
                        <label>Ownership: </label>
                        <select ref='userInput'
                        className='form-control'
                        onChange={this.onChangeOwnedOrLeased}>
                            <option key='Owned' value='Owned'>Owned</option>
                            <option key='Leased' value='Leased'>Leased</option>
                        </select>
                    </div>
                    <div className='form-group' style={{display: this.state.ownedOrLeased === 'Leased' ? 'block' : 'none'}}>
                        <label>Lease Expires: </label>
                        <div>
                            <DatePicker
                            selected={this.state.leaseExp}
                            onChange={this.onChangeLeaseExp} />
                        </div>
                    </div>
                    <div className='form-group'>
                        <label>Cost: </label>
                        <input type='number' 
                        className='form-control' 
                        value={this.state.cost} 
                        onChange={this.onChangeCost}/>
                    </div>

                    <div className='form-group'>
                        <label>Rego Done: </label>
                        <div>
                            <DatePicker
                            selected={this.state.regoDone}
                            onChange={this.onChangeRegoDone} />
                        </div>
                    </div>
                    <div className='form-group'>
                        <label>WOF Done: </label>
                        <div>
                            <DatePicker
                            selected={this.state.warrantDone}
                            onChange={this.onChangeWarrantDone} />
                        </div>
                    </div>
                    <div className='form-group'>
                        <label>Odometer Reading: </label>
                        <input type='number' 
                        required className='form-control' 
                        value={this.state.odometer} 
                        onChange={this.onChangeOdometer}/>
                    </div>
                    <div className='form-group'>
                        <label>Odometer Date: </label>
                        <div>
                            <DatePicker
                            selected={this.state.odometerDate}
                            onChange={this.onChangeOdometerDate} />
                        </div>
                    </div>
                    <div className='form-group'>
                    <a href='/'><div style={{float: 'left', marginRight: '15px'}} className='btn btn-primary'>Cancel</div></a>
                    </div>
                    <div className='form-group'>
                        <input type='submit' value='Add Vehicle' className='btn btn-primary'/>
                    </div>
                </form>
            </div>
        )
    }
}
