import React from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

let regoIsDue, warrantIsDue, daysToRego, daysToWarrant, today, regoWarning, warrantWarning



export default class VehicleList extends React.Component {

    state = {
        vehicles: []
    }

    componentDidMount() { // this method adds a list of vehicles from the server (MongoDB) to the state as array
        axios.get('http://localhost:5000/vehicles/')
        .then(res => {
            this.setState({
                vehicles: res.data
            })

            regoIsDue = new Date(res.data[0].regoDue)
            warrantIsDue = new Date(res.data[0].warrantDue)
            today = new Date()
            daysToRego = Math.floor((regoIsDue.getTime() - today.getTime()) / (1000 * 3600 * 24))
            daysToWarrant = Math.floor((warrantIsDue.getTime() - today.getTime()) / (1000 * 3600 * 24))
            console.log(daysToRego, daysToWarrant)

            if (daysToRego < 30 && daysToRego > 15 ) {regoWarning = 'text-warning'} else if (daysToRego < 15) { regoWarning='text-danger'} else if (daysToRego > 30) {}
            console.log(regoWarning)

            if (daysToWarrant < 30 && daysToWarrant > 15 ) {warrantWarning = 'text-warning'} else if (daysToWarrant < 15) { warrantWarning='text-danger'} else if (daysToWarrant > 30) {}
            console.log(warrantWarning)
        })
        .catch(err => console.log(err))
    }

    vehicleList = () => {
        return this.state.vehicles.map(currentvehicle => {
            return <Vehicle vehicle={currentvehicle} deleteVehicle={this.deleteVehicle} key={currentvehicle._id}/>
        })
    }

    deleteVehicle = (id) => {
        if (window.confirm("Are you sure?")) {
        axios.delete('http://localhost:5000/vehicles/'+id)
        .then(res => console.log(res.data))
        this.setState({
            vehicles: this.state.vehicles.filter(el => el._id !== id)
        })
        }
    }

    Vehicle = (props) => {
        <tr>
            <td>{props.vehicle.make}</td>
            <td>{props.vehicle.model}</td>
            <td>{props.vehicle.year}</td>
            <td>{props.vehicle.numberPlate}</td>
            <td>{props.vehicle.staff}</td>
            <td>{props.vehicle.ownedOrLeased}</td>
            <td>{props.vehicle.ownedOrLeased === 'Leased' ? props.vehicle.leaseExp.substring(0,10).split('-').reverse().join('/') : 'NA'}</td>
            <td>${props.vehicle.cost}</td>
            <td className={regoWarning}>{props.vehicle.regoDue.substring(0,10).split('-').reverse().join('/')}</td>
            <td className={warrantWarning}>{props.vehicle.warrantDue.substring(0,10).split('-').reverse().join('/')}</td>
            <td>{props.vehicle.odometer}</td>
            <td>{props.vehicle.odometerDate.substring(0,10).split('-').reverse().join('/')}</td>
            <td>
                <Link to={'/edit/'+props.vehicle._id}>edit</Link> | <a href='#' className='text-danger' onClick={() => {props.deleteVehicle(props.vehicle._id)}}>delete</a>
            </td>
        </tr>
    }

    render() {
        return(
            <div className='table-responsive'>
                <h3 style={{textAlign: 'center'}}>All Vehicles</h3>
                <table className='table'>
                    <thead className='thead-light'>
                        <tr>
                            <th>Make</th>
                            <th>Model</th>
                            <th>Year</th>
                            <th>Plate</th>
                            <th>Staff</th>
                            <th>Ownership</th>
                            <th>Lease Exp</th>
                            <th>Cost</th>
                            <th>Rego Due</th>
                            <th>WOF Due</th>
                            <th>Odo Read</th>
                            <th>Odo Date</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.vehicleList()}
                    </tbody>
                </table>
            </div>
        )
    }
}
