import React from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import axios from 'axios'

export default class CreateVehicle extends React.Component {

    state = {
        make: '',
        model: '',
        year: 0,
        numberPlate: '',
        staff: '',
        ownedOrLeased: ''
        // cost: 
        // purchasedLeased: 
        // regoDone: 
        // warrantDone: 
        // odometer: 
        // odometerDate: 
    }

    // DO I NEED componentDidMount method here? 

    onChangeMake = (e) => {
        this.setState({
            make: e.target.value
        })
    }

    onChangeModel = (e) => {
        this.setState({
            model: e.target.value
        })
    }

    onChangeYear = (e) => {
        this.setState({
            year: e.target.value
        })
    }

    onChangeNumberPlate = (e) => {
        this.setState({
            numberPlate: e.target.value
        })
    }

    onChangeStaff = (e) => {
        this.setState({
            staff: e.target.value
        })
    }

    onChangeOwnedOrLeased = (e) => {
        this.setState({
            ownedOrLeased: e.target.value
        })
    }
    // NEED TO ADD METHODS FOR ALL FIELDS

    onSubmit = (e) => {
        e.preventDefault()
        const vehicle = {
            make: this.state.make,
            model: this.state.model,
            year: this.state.model,
            numberPlate: this.state.model,
            staff: this.state.model,
            ownedOrLeased: this.state.model 
        }

        axios.post('http://localhost:5000/vehicles/add', vehicle)
        .then(res => console.log(res.data))

        window.location = '/'
    }
    // INSTALL DATEPICKER FOR ALL DATE ENTRIES
    render() {
        return(
            <div>
                <h3>Add Vehicle Entry</h3>
                <form onSubmit={this.onSubmit}>
                    <div className='form-group'>
                        <label>Make: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.make} 
                        onChange={this.onChangeMake}/>
                    </div>
                    <div className='form-group'>
                        <label>Model: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.model} 
                        onChange={this.onChangeModel}/>
                    </div>
                    <div className='form-group'>
                        <label>Year: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.year} 
                        onChange={this.onChangeYear}/>
                    </div>
                    <div className='form-group'>
                        <label>Number Plate: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.numberPlate} 
                        onChange={this.onChangeNumberPlate}/>
                    </div>
                    <div className='form-group'>
                        <label>Staff: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.staff} 
                        onChange={this.onChangeStaff}/>
                    </div>
                    <div className='form-group'>
                        <label>Owned/Leased: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.ownedOrLeased} 
                        onChange={this.onChangeOwnedOrLeased}/>
                    </div>
                    <div className='form-group'>
                        <input type='submit' value='Add Vehicle' className='btn btn-primary'/>
                    </div>
                </form>
            </div>
        )
    }
}
