import React from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import axios from 'axios'

export default class EditVehicle extends React.Component {

    state = {
        make: '',
        model: ''
        //year: 
        //numberPlate: 
        // staff: 
        // OwnedOrLeased:
        // cost: 
        // purchasedLeased: 
        // regoDone: 
        // warrantDone: 
        // odometer: 
        // odometerDate: 
    }

    componentDidMount() {
        axios.get('http://localhost:5000/vehicles/'+this.props.match.params.id)
        .then(res => {
            this.setState({
                make: res.data.make,
                model: res.data.model
            })
        })
        .catch(err => console.log(err))
    }

    onChangeMake = (e) => {
        this.setState({
            make: e.target.value
        })
    }

    onChangeModel = (e) => {
        this.setState({
            model: e.target.value
        })
    }

    // NEED TO ADD METHODS FOR ALL FIELDS

    onSubmit = (e) => {
        e.preventDefault()
        const vehicle = {
            make: this.state.make,
            model: this.state.model 
        }

        axios.post('http://localhost:5000/vehicles/update/'+this.props.match.params.id, vehicle)
        .then(res => console.log(res.data))

        window.location = '/'
    }
    // INSTALL DATEPICKER FOR ALL DATE ENTRIES
    render() {
        return(
            <div>
                <h3>Edit Vehicle Entry</h3>
                <form onSubmit={this.onSubmit}>
                    <div className='form-group'>
                        <label>Make: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.make} 
                        onChange={this.onChangeMake}/>
                    </div>
                    <div className='form-group'>
                        <label>Model: </label>
                        <input type='text' 
                        required className='form-control' 
                        value={this.state.model} 
                        onChange={this.onChangeModel}/>
                    </div>
                    <div className='form-group'>
                        <input type='submit' value='Update' className='btn btn-primary'/>
                    </div>
                </form>
            </div>
        )
    }
}
